import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
// import { connect } from "react-redux";
// import { Form } from 'react-bootstrap';

class LoginScreen extends Component {

    constructor(props) {
        super(props)
        this.state = {
            t: props.t,
            userName: '',
            password: '',
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
            [name]: value,
            // email_error: '',
            // password_error: '',
         });
    }

    handleSubmit(event) {
      const { userName, password } = this.state;
    }

    handleKeyPress = event => {
      if (event.key == 'Enter') {
        this.handleSubmit(event);
      }
    }

    componentDidMount() {

    }

    render() {
      const { userName, password, password_error, email_error } = this.state;
      return (
        <div className="container-fluid fulll-body" style={{ overflow: "auto" }} onKeyPress={e => this.handleKeyPress(e)}>
            <div className="row login-container ">
                <div className="col-md-7 col-lg-7  tab-sec">
                    <div className="row">
                        <div className="col-md-2 col-lg-2 col-sm-2 bg-blue"></div>
                        <div className="col-md-10 col-sm-10  d-lg-block col-lg-10 justify-content-center left-section" >
                        <div className="left-body-content">
                                <h1 style={{wordBreak: 'break-word'}}> {"maintext"} </h1>
                                <p style={{wordBreak: 'break-word'}}> {"subtext"} </p>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="col-md-12 col-lg-5 col-xs-12  col-sm-12 justify-content-center right-section">
                    <div className="left-body-container">
                        {/* <div className="img-responsive login-logo">
                            <img src={logo_as4} className="d-inline-block img-fluid" ></img>
                        </div> */}
                        <reactbootstrap.Form action="/" method="post" className="user-details">
                            <div className="form-group email-sec ">
                                <div className="login-email-head">
                                    {/* <span class="login-email-icon-sec"> <img src={icon_email} class="d-inline-block input-icon-sec "  ></img> </span> */}
                                    <span className="login-email-input-sec">
                                    {/* <span  className="icon_gmail">gmail</span> */}
                                    <span  className="icon_gmail"></span>
                                        <input
                                        name="userName"
                                        value={userName}
                                        type="email"
                                        className="form-control email-input"
                                        id="exampleInputEmail1"
                                        placeholder={"userName"}
                                        onChange={this.handleChange}
                                        autoComplete="off" />
                                     </span>
                                </div>
                                {/* <div style={{ color: 'red' }}>{email_error}</div> */}
                            </div>
                            <div className="form-group password-sec">
                                <div className="login-email-head">
                                    <span className="login-password-input-sec">
                                    <span className="icon_password"></span>
                                        <input
                                        name="password"
                                        value={password}
                                        type="password"
                                        className="form-control password-input"
                                        id="exampleInputPassword1"
                                        placeholder={"Password"}
                                        onChange={this.handleChange}
                                        autoComplete="off" />
                                    </span>
                                </div>
                                <div style={{ color: 'red' }}>{password_error}</div>
                            </div>
                            {/* {error &&
                                <div style={{ color: 'red' ,textAlign: "center" }}>{error}</div>
                            } */}
                        </reactbootstrap.Form>
                        <div className=" btn-login ">
                          <reactbootstrap.Button type="submit" onClick={e => this.handleSubmit(e)} className="log-in-btn">
                                {'Log in'}
                            </reactbootstrap.Button>
                        </div>
                    </div>
                </div>
            </div>
          </div>
      );
    }
}

export default (LoginScreen);
