import React, { Component } from 'react';
import './App.css';
import LoginScreen from './Login/LoginScreen';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import EmployeeListPage from './Login/EmployeeListPage'
import { createBrowserHistory } from 'history'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loginStatus: '',
    }

    this.reducer = this.reducer.bind(this);
  }

  reducer(state = this.state, action) {
    switch(action.type) {
      case 'LoginSuccess':
        this.setState({
          loginStatus: 'LoginSuccess',
        })
        return this.state

      default: return state
    }
  }

  render() {
    const NotFoundRedirect = () => <Redirect to='/loginscreen' />
    const history = createBrowserHistory();
    const localUser = localStorage.getItem('user');

    if (localUser !== undefined && localUser === null) {
      return (
        <div className="App">
          <header className="App-header">
              <BrowserRouter>
                <Switch>
                  <Route exact path='/loginscreen' {...history} component={LoginScreen} />
                  <Route component={NotFoundRedirect} />
                </Switch>
              </BrowserRouter>
          </header>
        </div>
      );
    } else {
      return (
        <div className="App">
          <header className="App-header">
              <BrowserRouter>
                <Switch>
                  <Route exact path='/manageEmployees' {...history} component={EmployeeListPage} />
                </Switch>
              </BrowserRouter>
          </header>
        </div>
      );
    }
  }
}

export default (App);
