import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
// import { Button, Container, Form, Input } from 'reactstrap';

// import { connect } from "react-redux";
// import { Form } from 'react-bootstrap';

class LoginScreen extends Component {
    constructor(props) {
        super(props)
        this.state = {
            userName: '',
            password: '',
            email_error: '',
            password_error: '',
            loginData: {'username' : 'hruday@gmail.com','password' : 'hruday123'},
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
            [name]: value,
            email_error: '',
            password_error: '',
         });
    }

    handleSubmit(event) {
      const { history } = this.props

      if (this.state.userName !== '' && this.state.password !== '') {
        console.log(this.state.userName);
        console.log(this.state.password);
        history.push('/manageEmployees')
      }
      //validations
      //required
      // const { userName, password } = this.state;
    }

    handleKeyPress = event => {
      if (event.key === 'Enter') {
        this.handleSubmit(event);
      }
    }

    componentDidMount() {

    }

    render() {
      console.log(this.state.loginData);

      const { userName, password, password_error, email_error } = this.state;
      return (
        <reactbootstrap.Form action="/" method="post" className="user-details">
          <div style={{ marginbottom: '15px' }}>
              {/* <span className="login-email-input-sec">
              <span  className="icon_gmail"></span> */}
                <input
                  name="userName"
                  value={userName}
                  type="email"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black"}}
                  // className="form-control email-input"
                  id="exampleInputEmail1"
                  placeholder={"userName"}
                  onChange={this.handleChange}
                  autoComplete="off" />
               {/* </span> */}
          </div>
          {/* <div style={{ color: 'red' }}>{email_error}</div> */}
          <div style={{ marginbottom: '15px' }}>
              {/* <span className="login-password-input-sec">
              <span className="icon_password"></span> */}
                <input
                  name="password"
                  value={password}
                  type="password"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black" }}
                  // className="form-control password-input"
                  id="exampleInputPassword1"
                  placeholder={"Password"}
                  onChange={this.handleChange}
                  autoComplete="off" />
              {/* </span> */}
          </div>
          {/* <div style={{ color: 'red' }}>{password_error}</div> */}
          <div className="btn-login">
            <reactbootstrap.Button type="button" onClick={e => this.handleSubmit(e)} className="log-in-btn">
                  {'Login'}
              </reactbootstrap.Button>
          </div>
        </reactbootstrap.Form>
      );
    }
}

export default (LoginScreen);
