import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import EmployeeListPage from '../Login/EmployeeListPage'
// import { history } from 'react-router/lib/BrowserHistory'
// import { Button, Container, Form, Input } from 'reactstrap';
// import { connect } from "react-redux";
// import { Form } from 'react-bootstrap';

class LoginScreen extends Component {
    constructor(props) {
        super(props)
        this.state = {
            userName: '',
            password: '',
            emailRequired: false,
            passwordRequired: false,
            loginData: {'username' : 'hruday@gmail.com','password' : 'hruday123'},
            UserExist: true,
            UserError: '',
            validEmail: true,
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
            [name]: value,
            emailRequired: false,
            passwordRequired: false,
            validEmail: true,
            // UserError: '',
         });
    }

    handleSubmit(event) {
      // const { history } = this.props
      // console.log(history);
      const { userName, password, loginData } = this.state
      let userEmailValidation = (new RegExp(/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,15}/g).test(userName)) ? true : false;

      if (userName !== '' && password !== '') {
        if (userEmailValidation) {
          if (userName === loginData.username && password === loginData.password) {
            localStorage.setItem('user', 1);
            window.location.href = '/manageEmployees';
            // return  <Redirect  to="/manageEmployees" />
            // this.props.history.push('/manageEmployees');
            // const RedirectPath = <Redirect to='/manageEmployees' />
            // return <Redirect to='/manageEmployees'  />
            // history.push('/manageEmployees')
          } else {
            this.setState({
              UserExist: false,
              UserError: (userName !== loginData.username) ? 'UserName does not Exists' : 'Password is wrong',
            })
          }
        } else {
          this.setState({
            validEmail: userEmailValidation,
          })
        }
      } else {
        this.setState({
          emailRequired: userName === '' ? true : false,
          passwordRequired: password === '' ? true : false,
        })
      }
    }

    handleKeyPress = event => {
      if (event.key === 'Enter') {
        this.handleSubmit(event);
      }
    }

    componentDidMount() {

    }

    render() {
      const { userName, password, passwordRequired, emailRequired, UserExist, UserError, validEmail } = this.state;
      return (
        <div className="" style={{ overflow: "auto" }} onKeyPress={e => this.handleKeyPress(e)}>
          <reactbootstrap.Form action="/" method="post">
            <div style={{ marginbottom: '15px' }}>
                {/* <span className="login-email-input-sec">
                <span  className="icon_gmail"></span> */}
                  <input
                    name="userName"
                    value={userName}
                    type="email"
                    style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px'}}
                    // className="form-control email-input"
                    id="exampleInputEmail1"
                    placeholder={"userName"}
                    onChange={this.handleChange}
                    autoComplete="off" />
                 {/* </span> */}
            </div>
            {emailRequired === true &&
              <div style={{ color: 'red' }}>{"userName field is required"}</div>
            }
            {validEmail === false &&
              <div style={{ color: 'red' }}>{"Please enter a proper userName"}</div>
            }
            <div style={{ marginbottom: '15px' }}>
                {/* <span className="login-password-input-sec">
                <span className="icon_password"></span> */}
                  <input
                    name="password"
                    value={password}
                    type="password"
                    style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px' }}
                    // className="form-control password-input"
                    id="exampleInputPassword1"
                    placeholder={"Password"}
                    onChange={this.handleChange}
                    autoComplete="off" />
                {/* </span> */}
            </div>
            {passwordRequired === true &&
              <div style={{ color: 'red' }}>{"Password field is required"}</div>
            }
            <div>
              <reactbootstrap.Button type="button" onClick={e => this.handleSubmit(e)}>
                    {'Login'}
                </reactbootstrap.Button>
            </div>
            {UserExist === false &&
              <div style={{ color: 'red' }}>{UserError}</div>
            }
          </reactbootstrap.Form>
        </div>
      );
    }
}

export default (LoginScreen);
