import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import EmployeeListPage from '../Login/EmployeeListPage'
// import { history } from 'react-router/lib/BrowserHistory'
// import { Button, Container, Form, Input } from 'reactstrap';
// import { connect } from "react-redux";
// import { Form } from 'react-bootstrap';

class LoginScreen extends Component {
    constructor(props) {
        super(props)
        this.state = {
            userName: '',
            password: '',
            email_error: false,
            password_error: false,
            loginData: {'username' : 'hruday@gmail.com','password' : 'hruday123'},
            UserExist: true,
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
            [name]: value,
            email_error: '',
            password_error: '',
         });
    }

    handleSubmit(event) {
      // const { history } = this.props
      // console.log(history);
      const { userName, password, loginData } = this.state
      if (userName !== '' && password !== '') {
        if (userName === loginData.username && password === loginData.password) {
          console.log(this.state.userName);
          console.log(this.state.password);
          console.log(loginData);
          localStorage.setItem('user', 1);
          window.location.href = '/manageEmployees';
          // return  <Redirect  to="/manageEmployees" />
          // this.props.history.push('/manageEmployees');
          // const RedirectPath = <Redirect to='/manageEmployees' />
          //
          // return (
          //   <BrowserRouter>
          //     <Switch>
          //       <Route to = "/manageEmployees" component={EmployeeListPage} />
          //       {/* <Route component={RedirectPath} /> */}
          //     </Switch>
          //   </BrowserRouter>
          // )
          // return <Redirect to='/manageEmployees'  />
          // history.push('/manageEmployees')
        } else {
          this.setState({
            UserExist: false,
          })
        }
      } else {
        this.setState({
          email_error: userName === '' ? true : false,
          password_error: password === '' ? true : false,
        })
      }
      //validations
      //required
    }

    handleKeyPress = event => {
      if (event.key === 'Enter') {
        this.handleSubmit(event);
      }
    }

    componentDidMount() {

    }

    render() {
      console.log(this.state.loginData);

      const { userName, password, password_error, email_error, UserExist } = this.state;
      return (
        <reactbootstrap.Form action="/" method="post">
          <div style={{ marginbottom: '15px' }}>
              {/* <span className="login-email-input-sec">
              <span  className="icon_gmail"></span> */}
                <input
                  name="userName"
                  value={userName}
                  type="email"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px'}}
                  // className="form-control email-input"
                  id="exampleInputEmail1"
                  placeholder={"userName"}
                  onChange={this.handleChange}
                  autoComplete="off" />
               {/* </span> */}
          </div>
          {email_error === true &&
            <div style={{ color: 'red' }}>{"userName field is required"}</div>
          }
          <div style={{ marginbottom: '15px' }}>
              {/* <span className="login-password-input-sec">
              <span className="icon_password"></span> */}
                <input
                  name="password"
                  value={password}
                  type="password"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px' }}
                  // className="form-control password-input"
                  id="exampleInputPassword1"
                  placeholder={"Password"}
                  onChange={this.handleChange}
                  autoComplete="off" />
              {/* </span> */}
          </div>
          {password_error === true &&
            <div style={{ color: 'red' }}>{"Password field is required"}</div>
          }
          <div>
            <reactbootstrap.Button type="button" onClick={e => this.handleSubmit(e)}>
                  {'Login'}
              </reactbootstrap.Button>
          </div>
          {UserExist === false &&
            <div style={{ color: 'red' }}>{"User does not exist"}</div>
          }
        </reactbootstrap.Form>
      );
    }
}

export default (LoginScreen);
