import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import { Button, Container, Form, Input } from 'reactstrap';

// import { connect } from "react-redux";
// import { Form } from 'react-bootstrap';

class LoginScreen extends Component {

    constructor(props) {
        super(props)
        this.state = {
            t: props.t,
            userName: '',
            password: '',
            email_error: '',
            password_error: '',
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({
            [name]: value,
            email_error: '',
            password_error: '',
         });
    }

    handleSubmit(event) {
      // const { userName, password } = this.state;
    }

    handleKeyPress = event => {
      if (event.key === 'Enter') {
        this.handleSubmit(event);
      }
    }

    componentDidMount() {

    }

    render() {
      const { userName, password, password_error, email_error } = this.state;
      return (
        <reactbootstrap.Container className="p-1">
          <reactbootstrap.Form action="/" method="post" className="user-details">
            <div style={{ marginbottom: '15px' }}>
                {/* <span className="login-email-input-sec">
                <span  className="icon_gmail"></span> */}
                  <input
                    name="userName"
                    value={userName}
                    type="email"
                    // className="form-control email-input"
                    id="exampleInputEmail1"
                    placeholder={"userName"}
                    onChange={this.handleChange}
                    autoComplete="off" />
                 {/* </span> */}
            </div>
            {/* <div style={{ color: 'red' }}>{email_error}</div> */}
            <div style={{ marginbottom: '15px' }}>
                {/* <span className="login-password-input-sec">
                <span className="icon_password"></span> */}
                  <input
                    name="password"
                    value={password}
                    type="password"
                    // className="form-control password-input"
                    id="exampleInputPassword1"
                    placeholder={"Password"}
                    onChange={this.handleChange}
                    autoComplete="off" />
                {/* </span> */}
            </div>
            {/* <div style={{ color: 'red' }}>{password_error}</div> */}
            <div  className="col-md-8">
              <Input
                class="col-md-12"
                id="confirmpassword"
                type="password"
                name="confirmpassword"
                placeholder={"Confirm password"}
                value={this.state.confirmpassword}
                onChange ={this.handleChange}
                autoComplete="off"
              />
            </div>
            <div className="btn-login">
              <reactbootstrap.Button type="submit" onClick={e => this.handleSubmit(e)} className="log-in-btn">
                    {'Login'}
                </reactbootstrap.Button>
            </div>
          </reactbootstrap.Form>
        </reactbootstrap.Container>
      );
    }
}

export default (LoginScreen);
